<?php function add_meta_title ($string){	$CI =& get_instance();	$CI->data['meta_title'] = $string . ' - ' . $CI->data['meta_title'];}function add_meta_description ($string){	$CI =& get_instance();	$desc = strip_tags($string);	$CI->data['meta_description'] = character_limiter($desc,160);}function add_meta_url($string){	$CI =& get_instance();	$CI->data['meta_url'] = $string;}function add_meta_image($dir,$image){	$CI =& get_instance();	$CI->data['meta_image'] = site_url($dir.$image);}